INSERT INTO `fa_banner` VALUES (2,'北京市海淀区温泉镇生态景观绩效评价','/uploads/20221029/1e511f20fcf79c97d900ca25cddd7dab.jpg',2,'0',1,1,0,'温泉镇镇域面积33.19平方公里，三面环山，镇域内植被丰沛、林木覆盖率较高，生态资源优势明显，整体乡村生态景观评价较为良好。','0',125),(3,'河北正定镇滹沱河生态修复工程景观绩效评价','/uploads/20221029/b1aff7a5bb7e8cea9bfca317e6cec81b.png',2,'',1,1,0,'研究区域面积2585.56公顷，2020年，经过生态修复工程，滹沱河一期城区段绿地面积明显增加，形成了“蓝绿交织、疏林花海、开阔水面”的景观，整体乡村生态景观评价较为良好。','0',104),(4,'新建城市公园绿地','/uploads/20221104/600d51f42671b99f48f7d0c7a374c7c2.jpg',3,'0',0,1,0,'从安全韧性、生态调节、生物多样性、健康舒适和创新活力五个维度对城市公园绿地进行科学评价','景观绩效评价项目',0),(5,'北京市浅山区青龙湖森林公园景观绩效评价','/uploads/20221112/9c2e74173571e7cf07459f321ba5dc74.jpg',2,'0',0,1,0,'公园总面积1343公顷，定位为生态屏障门户节点，园内山清水秀，林木茂盛，湖面宽阔，生态环境十分优美，整体城市公园绿地景观评价较为良好。','景观绩效评价项目',165),(6,'新建乡村生态','/uploads/20221029/c61432b6db086e61f5a74fc386ed924c.jpg',4,'0',0,1,0,'从安全韧性、生态调节、生物多样性、健康舒适和创新活力五个维度对乡村生态景观进行科学评价','景观绩效评价项目',0),(7,'福建省将乐县常口村生态景观绩效评价','/uploads/20221029/c61432b6db086e61f5a74fc386ed924c.jpg',2,'0',0,1,0,'福建省将乐县，“两山理论”孕育地，综合性文旅康养基地 统筹乡村山水林田湖各要素的乡村生态景观评价实践','福建省将乐县，“两山理论”孕育地，综合性文旅康养基地 统筹乡村山水林田湖各要素的乡村生态景观评价实践',117);
